import React, { Component } from "react";
import {
  Button,
  View,
  Text,
  StyleSheet,
  TouchableOpacity,    
  StatusBar,
  TextInput,
  Image,
} from "react-native";
import { FontAwesome } from '@expo/vector-icons';
import { FlatList } from "react-native-gesture-handler";


export default class SearchBooks extends Component {
  constructor() {
    super();
    this.state = {
      query: null,
      dataSource: [],
      dataBackup: [],
    };
  }

  componentDidMount() {
    var data = [
      {
        name: "The Last Battle",
        author: "C. S. Lewis",
        img: "data:image/jpeg;base64,",
        description:
          "The Last Battle is a high fantasy novel for children by C. S. Lewis, published by The Bodley Head in 1956. It was the seventh and final novel in The Chronicles of Narnia (1950–1956). Like the other novels in the series, it was illustrated by Pauline Baynes and her work has been retained in many later editions.",
        
      },
      {
        name: "Harry Potter",
        author: "J. K. Rowling",
        img: "data:image/jpeg;base64,",
        description:
          "The Last Battle is a high fantasy novel for children by C. S. Lewis, published by The Bodley Head in 1956. It was the seventh and final novel in The Chronicles of Narnia (1950–1956). Like the other novels in the series, it was illustrated by Pauline Baynes and her work has been retained in many later editions..",
       
      },
      {
        name: "Invisible Man",
        author: " Ralph Ellison",
        img: "data:image/jpeg;base64,",
        description:
          "The Last Battle is a high fantasy novel for children by C. S. Lewis, published by The Bodley Head in 1956. It was the seventh and final novel in The Chronicles of Narnia (1950–1956). Like the other novels in the series, it was illustrated by Pauline Baynes and her work has been retained in many later editions.",
        
      },
      {
        name: "Mrs Dalloway",
        author: "Virginia Woolf",
        img: "data:image/jpeg;base64,",
        description:
          "The Last Battle is a high fantasy novel for children by C. S. Lewis, published by The Bodley Head in 1956. It was the seventh and final novel in The Chronicles of Narnia (1950–1956). Like the other novels in the series, it was illustrated by Pauline Baynes and her work has been retained in many later editions.",
       
      },
      
    ];

    this.setState({
      dataBackup: data,
      dataSource: data,
    });
  }

  filterItem = (event) => {
    var query = event.nativeEvent.text;
    this.setState({
      query: query,
    });
    if (query == "") {
      this.setState({
        dataSource: this.state.dataBackup,
      });
    } else {
      var data = this.state.dataBackup;
      query = query.toLowerCase();
      data = data.filter((l) => l.name.toLowerCase().match(query));

      this.setState({
        dataSource: data,
      });
    }
  };

  separator = () => {
    return (
      <View style={{ height: 10, width: "100%", backgroundColor: "#121212" }} />
    );
  };

  render() {
    return (
      <View style={styles.container}>
        <StatusBar barStyle="light-content" />
        
        <View style = {styles.design}>
     <FontAwesome style={styles.icon} name="search" size={24} color="white" />
      <TextInput style = {styles.searchBar} placeholder="Search here"
            
            value={this.state.query}
            onChange={this.filterItem.bind(this)}
            
          />
        </View>

        <FlatList
          data={this.state.dataSource}
          ItemSeparatorComponent={() => this.separator()}
          renderItem={({ item }) => {
            return (
              <TouchableOpacity onPress={item.press}>
                <View style={styles.bookContainer}>
                  <Image style={styles.image} source={{ uri: item.img }} />
                  <View style={styles.dataContainer}>
                    <Text numberOfLines={2} style={styles.title}>
                      {item.name}
                    </Text>
                    <Text style={styles.author}>{item.author}</Text>
                    <Text numberOfLines={4} style={styles.description}>
                      {item.description}
                    </Text>
                  </View>
                </View>
              </TouchableOpacity>
            );
          }}
        />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#121212',
  },
  design:{
    flexDirection: 'row',
    height: 35,
    width:"90%",
    margin: 10,
    borderWidth: 1,
    paddingRight:5,
    paddingLeft:5,
    backgroundColor:"#f4f4f4",
    borderRadius:8,
    borderColor:"#fadadd"
     
  },
  icon:{
    marginRight:10,
    marginTop:5,
    color:'#c4c3d0'
    
  },
  bookContainer: {
    flexDirection: "row",
    padding: 5,
    backgroundColor: "#413F3F",
    borderRadius: 20
  },
  image: {
    height: 150,
    width: 100,
    marginLeft: 5,
  },
  dataContainer: {
    padding: 10,
    paddingTop: 5,
    width: 250,
  },
  title: {
    fontSize: 18,
    fontWeight: "bold",
    color: "#CAC5C5",
  },
  description: {
    fontSize: 16,
    color: "#DAD6D6",
    marginTop: 10,
  },
  author: {
    fontSize: 16,
  },
 
});